const body=document.querySelector('body');document.querySelector('#yellowSchema').addEventListener('click',toggleYellow);document.querySelector('#blueSchema').addEventListener('click',toggleBlue);document.querySelector('#greenSchema').addEventListener('click',toggleGreen);document.querySelector('#purpleSchema').addEventListener('click',togglePurple);function toggleBlue(){$('#liveToastTheme').toast('show');if(body.classList.contains('blueAccent')){body.removeAttribute('class');localStorage.setItem("theme"," ")}else{body.removeAttribute('class');body.classList.add('blueAccent');localStorage.setItem("theme","blue")}}function toggleGreen(){$('#liveToastTheme').toast('show');if(body.classList.contains('greenAccent')){body.removeAttribute('class');localStorage.setItem("theme"," ")}else{body.removeAttribute('class');body.classList.add('greenAccent');localStorage.setItem("theme","green");bootstrap.Toast(document.getElementById("liveToastTheme")).show()}}function toggleYellow(){$('#liveToastTheme').toast('show');if(body.classList.contains('yellowAccent')){body.removeAttribute('class');localStorage.setItem("theme"," ")}else{body.removeAttribute('class');body.classList.add('yellowAccent');localStorage.setItem("theme","yellow");bootstrap.Toast(document.getElementById("liveToastTheme")).show()}}function togglePurple(){$('#liveToastTheme').toast('show');if(body.classList.contains('purpleAccent')){body.removeAttribute('class');localStorage.setItem("theme"," ")}else{body.removeAttribute('class');body.classList.add('purpleAccent');localStorage.setItem("theme","purple");bootstrap.Toast(document.getElementById("liveToastTheme")).show()}}var platte=localStorage.getItem("theme");if(platte==="dark"){body.classList.add('dark')};if(platte==="blue"){body.classList.add('blueAccent')};if(platte==="yellow"){body.classList.add('yellowAccent')};if(platte==="green"){body.classList.add('greenAccent')};if(platte==="purple"){body.classList.add('purpleAccent')};